// --- 2. GLOBAL VARIABLES AND DOM ELEMENTS (UNMODIFIED) ---
const recipeGallery = document.getElementById('recipe-gallery');
const searchInput = document.getElementById('search-input');
const searchButton = document.getElementById('search-btn');
const recipeModal = document.getElementById('recipe-modal');
const modalBody = document.getElementById('modal-body');
const closeBtn = document.querySelector('.close-btn');
const categoryNav = document.querySelector('.category-nav');
const subcategoryFilter = document.getElementById('subcategory-filter');
const regionSelect = document.getElementById('region-select');


// --- 3. FILTERING AND RENDERING FUNCTIONS (UNMODIFIED) ---

/**
 * Filters the global recipe list based on a type and value, then renders the results.
 */
function filterAndRenderRecipes(filterType, filterValue) {
    const term = filterValue.toLowerCase();
    
    // Hide the region filter by default
    subcategoryFilter.style.display = 'none';

    const filteredRecipes = ALL_RECIPES.filter(recipe => {
        const name = recipe.name.toLowerCase();
        const cuisine = recipe.cuisine.toLowerCase();
        const type = recipe.type.toLowerCase();
        const region = recipe.region.toLowerCase();

        if (filterType === 'all') {
            return true;
        } else if (filterType === 'search') {
            return name.includes(term);
        } else if (filterType === 'cuisine') {
            // Show region filter for Indian food only
            if (filterValue === 'Indian') subcategoryFilter.style.display = 'block';
            return cuisine === term;
        } else if (filterType === 'region') {
            return region === term;
        } else if (filterType === 'type') {
            return type === term;
        } else if (filterType === 'tag') {
            // Check if the recipe's tags array includes the tag value (e.g., 'spicy')
            return recipe.tags.includes(term);
        }
        return false;
    });

    renderRecipeCards(filteredRecipes);

    // After filtering to Indian, apply the current region dropdown state
    if (filterValue === 'Indian') {
        applyRegionFilter(regionSelect.value);
    }
}


// Function to create and inject the recipe card HTML
function renderRecipeCards(recipes) {
    recipeGallery.innerHTML = '';
    
    if (recipes.length === 0) {
        recipeGallery.innerHTML = '<p>No recipes found matching your filter criteria.</p>';
        return;
    }

    recipes.forEach(recipe => {
        // Prepare tags and regions for display
        let regionDisplay = recipe.region && recipe.region !== 'International' ? ` | ${recipe.region}` : '';
        let spicyTag = recipe.tags.includes('spicy') ? ' 🌶️' : '';
        
        const cardHtml = `
            <div class="recipe-card" data-id="${recipe.id}" data-cuisine="${recipe.cuisine}" data-region="${recipe.region}">
                <img src="${recipe.image}" alt="${recipe.name}">
                <div class="card-content">
                    <h3>${recipe.name}${spicyTag}</h3>
                    <p>Type: ${recipe.type}${regionDisplay}</p>
                    <button class="view-btn">View Recipe</button>
                </div>
            </div>
        `;
        recipeGallery.insertAdjacentHTML('beforeend', cardHtml);
    });
}

// Function to display the recipe details in the modal
function displayModalDetails(recipe) {
    let ingredientsList = recipe.ingredients.map(ing => `<li>${ing}</li>`).join('');
    let instructions = recipe.instructions.map((step, index) => `${index + 1}. ${step}`).join('<br>');
    
    modalBody.innerHTML = `
        <h2>${recipe.name}</h2>
        <img src="${recipe.image}" alt="${recipe.name}" style="width:100%; max-height: 400px; object-fit: cover; border-radius: 8px;">
        <p><strong>Cuisine:</strong> ${recipe.cuisine}</p>
        <p><strong>Region:</strong> ${recipe.region || 'N/A'}</p>
        <p><strong>Description:</strong> ${recipe.description}</p>
        
        <h3>Ingredients:</h3>
        <ul class="ingredients-list">${ingredientsList}</ul>

        <h3>Instructions:</h3>
        <p class="instructions-text">${instructions}</p>
    `;
    recipeModal.style.display = 'block';
}


// --- 4. CLIENT-SIDE FILTERING LOGIC (UNMODIFIED) ---

// Hides/shows region select and applies 'active' class
function updateCategoryState(filterValue) {
    // 1. Update Active Class
    document.querySelectorAll('.nav-btn').forEach(btn => btn.classList.remove('active-filter'));
    const activeBtn = document.querySelector(`[data-filter-value="${filterValue}"]`);
    if (activeBtn) activeBtn.classList.add('active-filter');
}

// Function to handle the North/South Indian filtering
function applyRegionFilter(selectedRegion) {
    // We only filter the Indian cards currently displayed in the gallery
    const cards = document.querySelectorAll('.recipe-card[data-cuisine="Indian"]'); 

    cards.forEach(card => {
        const cardRegion = card.getAttribute('data-region');
        
        if (selectedRegion === 'all-indian') {
            card.style.display = 'block';
        } else if (cardRegion === selectedRegion) {
            card.style.display = 'block';
        } else {
            card.style.display = 'none';
        }
    });
}


// --- 5. EVENT LISTENERS (UNMODIFIED) ---

// Category Navigation Click Handler (Event Delegation)
categoryNav.addEventListener('click', (e) => {
    const target = e.target.closest('.nav-btn');
    if (target) {
        const filterType = target.getAttribute('data-filter-type');
        const filterValue = target.getAttribute('data-filter-value');
        
        filterAndRenderRecipes(filterType, filterValue);
        updateCategoryState(filterValue);
    }
});

// Region Selection Change Handler
regionSelect.addEventListener('change', () => {
    applyRegionFilter(regionSelect.value);
});

// Search functionality
searchButton.addEventListener('click', () => {
    const searchTerm = searchInput.value.trim();
    if (searchTerm === "") return;
    filterAndRenderRecipes('search', searchTerm);
    updateCategoryState(null); // Remove active class from categories
});

// Event Delegation for viewing recipes
recipeGallery.addEventListener('click', (e) => {
    const target = e.target;
    if (target.classList.contains('view-btn')) {
        const card = target.closest('.recipe-card');
        const recipeId = card.getAttribute('data-id');
        
        // Find the full recipe object from the local data
        const recipe = ALL_RECIPES.find(r => r.id === recipeId);
        
        if (recipe) {
            displayModalDetails(recipe);
        }
    }
});

// Modal close functionality
closeBtn.addEventListener('click', () => {
    recipeModal.style.display = 'none';
});

// Close modal if user clicks outside of it
window.addEventListener('click', (e) => {
    if (e.target === recipeModal) {
        recipeModal.style.display = 'none';
    }
});

// --- 6. INITIAL LOAD (UNMODIFIED) ---
document.addEventListener('DOMContentLoaded', () => {
    // Initial display of all recipes
    filterAndRenderRecipes('all', 'all'); 
    updateCategoryState('all');
});